    private void btn_rechActionPerformed(java.awt.event.ActionEvent evt) {
        // TODO add your handling code here:
        String nom = txt_rech.getText();
        
        if (nom.equalsIgnoreCase("")){
            JOptionPane.showMessageDialog(this,"Vous devez entrer un valeur dans le champs!!!", "Erreur",JOptionPane.ERROR_MESSAGE);
        }else{
             tb_retour.setModel(new javax.swing.table.DefaultTableModel(
            new Retour().lister_Retour(nom),
            new String [] {
                "Code", "Articles", "Date Retour", "Quantites", "Qte Utilisables", "Qte defectueuses", "Personnes", "Commentaires"
            }
        ) {
            boolean[] canEdit = new boolean [] {
                false, false, false, false, false, false, false, false
            };

            @Override
            public boolean isCellEditable(int rowIndex, int columnIndex) {
                return canEdit [columnIndex];
            }
        });
        jScrollPane1.setViewportView(tb_retour);
        }
    }

    
